import React, { useEffect, useState } from "react";
import axios from "axios";
import SummarySection from "../components/SummarySection"; // Make sure this file exists
import jsPDF from "jspdf";
import "jspdf-autotable";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";

const HisabTally = ({ filters }) => {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const { partyId, factoryId, startDate, endDate } = filters;

  useEffect(() => {
    const fetchSummary = async () => {
      if (!partyId && !factoryId) return;
      setLoading(true);
      setError("");

      try {
        const baseUrl = partyId
          ? `http://localhost:5001/api/parties/${partyId}/summary`
          : `http://localhost:5001/api/factories/${factoryId}/summary`;

        const params = {};
        if (startDate) params.from = startDate;
        if (endDate) params.to = endDate;

        const response = await axios.get(baseUrl, { params });
        setSummary(response.data);
      } catch (error) {
        console.error("Failed to fetch hisab summary", error);
        setError("Error fetching summary. Please try again.");
        setSummary(null);
      } finally {
        setLoading(false);
      }
    };

    fetchSummary();
  }, [partyId, factoryId, startDate, endDate]);

  if (!partyId && !factoryId) {
    return <p className="text-gray-500">Please select a party or factory to view summary.</p>;
  }


  if (loading) return <p>Loading summary...</p>;
  if (error) return <p className="text-red-600">{error}</p>;
  if (!summary) return <p>No data available for selected filters.</p>;

  // Derive display values
  const total = summary.totalAmount || 0;
  const paidOrReceived = partyId ? summary.totalPaid : summary.totalReceived;
  const remaining = summary.remaining || 0;

  // === Export to PDF function ===
  const exportSummaryAsPDF = () => {
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Hisab Tally Summary Report", 14, 20);

    doc.setFontSize(12);
    const summaryData = [
      ["Total Amount", `â‚¹ ${total}`],
      [partyId ? "Total Paid" : "Total Received", `â‚¹ ${paidOrReceived}`],
      ["Remaining", `â‚¹ ${remaining}`],
    ];

    doc.autoTable({
      startY: 30,
      head: [["Field", "Value"]],
      body: summaryData,
      theme: "striped",
    });

    // Transactions table
    if (summary.transactions && summary.transactions.length > 0) {
      const txColumns = ["Date", "Amount", "Weight", "Rate"];
      const txRows = summary.transactions.map((tx) => [
        tx.date,
        `â‚¹ ${tx.amount}`,
        tx.weight,
        tx.rate,
      ]);
      doc.autoTable({
        startY: doc.lastAutoTable.finalY + 10,
        head: [txColumns],
        body: txRows,
        theme: "grid",
        headStyles: { fillColor: [41, 128, 185] },
        styles: { fontSize: 9 },
      });
    }

    // Payments table
    if (summary.payments && summary.payments.length > 0) {
      const payColumns = ["Date", "Amount"];
      const payRows = summary.payments.map((pay) => [pay.date, `â‚¹ ${pay.amount}`]);
      doc.autoTable({
        startY: doc.lastAutoTable.finalY + 10,
        head: [payColumns],
        body: payRows,
        theme: "grid",
        headStyles: { fillColor: [41, 128, 185] },
        styles: { fontSize: 9 },
      });
    }

    doc.save("hisab-tally-summary.pdf");
  };

  // === Export to Excel function ===
  const exportSummaryAsExcel = () => {
    // Prepare summary sheet data
    const summarySheetData = [
      { Field: "Total Amount", Value: total },
      { Field: partyId ? "Total Paid" : "Total Received", Value: paidOrReceived },
      { Field: "Remaining", Value: remaining },
    ];

    // Transactions data
    const transactionsData = summary.transactions
      ? summary.transactions.map((tx) => ({
          Date: tx.date,
          Amount: tx.amount,
          Weight: tx.weight,
          Rate: tx.rate,
        }))
      : [];

    // Payments data
    const paymentsData = summary.payments
      ? summary.payments.map((pay) => ({
          Date: pay.date,
          Amount: pay.amount,
        }))
      : [];

    // Create workbook and append sheets
    const wb = XLSX.utils.book_new();

    const summarySheet = XLSX.utils.json_to_sheet(summarySheetData);
    XLSX.utils.book_append_sheet(wb, summarySheet, "Summary");

    if (transactionsData.length > 0) {
      const txSheet = XLSX.utils.json_to_sheet(transactionsData);
      XLSX.utils.book_append_sheet(wb, txSheet, "Transactions");
    }

    if (paymentsData.length > 0) {
      const paySheet = XLSX.utils.json_to_sheet(paymentsData);
      XLSX.utils.book_append_sheet(wb, paySheet, "Payments");
    }

    const wbout = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    const blob = new Blob([wbout], { type: "application/octet-stream" });
    saveAs(blob, "hisab-tally-summary.xlsx");
  };

  return (
    <div className="bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">
        {partyId ? "Party Summary" : "Factory Summary"}
      </h2>

      {/* Summary cards */}
      <SummarySection
        label={partyId ? "Total Paid" : "Total Received"}
        total={total}
        paidOrReceived={paidOrReceived}
        remaining={remaining}
      />

      {/* Export Buttons */}
      <div className="mb-6 flex gap-4">
  <button
    onClick={exportSummaryAsPDF}
    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 flex items-center gap-2"
    title="Download summary as PDF"
  >
    ðŸ“„ Export PDF
  </button>
  <button
    onClick={exportSummaryAsExcel}
    className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 flex items-center gap-2"
    title="Download summary as Excel"
  >
    ðŸ“Š Export Excel
  </button>
</div>


      {/* Transactions Table */}
      <div className="mb-6">
        <h3 className="text-xl font-bold mb-2">Transactions</h3>
        {summary.transactions.length === 0 ? (
          <p>No transactions.</p>
        ) : (
          <table className="w-full border text-sm">
            <thead>
              <tr className="bg-gray-200">
                <th className="border px-2 py-1 text-left">Date</th>
                <th className="border px-2 py-1 text-left">Amount</th>
                <th className="border px-2 py-1 text-left">Weight</th>
                <th className="border px-2 py-1 text-left">Rate</th>
              </tr>
            </thead>
            <tbody>
              {summary.transactions.map((tx) => (
                <tr key={tx.id}>
                  <td className="border px-2 py-1">{tx.date}</td>
                  <td className="border px-2 py-1">â‚¹ {tx.amount}</td>
                  <td className="border px-2 py-1">{tx.weight}</td>
                  <td className="border px-2 py-1">{tx.rate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* Payments Table */}
      <div>
        <h3 className="text-xl font-bold mb-2">{partyId ? "Payments Made" : "Payments Received"}</h3>
        {summary.payments.length === 0 ? (
          <p>No payments.</p>
        ) : (
          <table className="w-full border text-sm">
            <thead>
              <tr className="bg-gray-200">
                <th className="border px-2 py-1 text-left">Date</th>
                <th className="border px-2 py-1 text-left">Amount</th>
              </tr>
            </thead>
            <tbody>
              {summary.payments.map((pay) => (
                <tr key={pay.id}>
                  <td className="border px-2 py-1">{pay.date}</td>
                  <td className="border px-2 py-1">â‚¹ {pay.amount}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default HisabTally;
