import React, { useEffect, useState } from "react";
import axios from "axios";
import SummarySection from "../components/SummarySection";
import jsPDF from "jspdf";
import "jspdf-autotable";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import { formatCurrency, formatDate } from "../utils/formatter";

const HisabTally = ({ filters }) => {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const { partyId, factoryId } = filters;

  useEffect(() => {
    const fetchSummary = async () => {
      if (!partyId && !factoryId) {
        setSummary(null);
        return;
      }

      setLoading(true);
      setError("");

      try {
        const url = partyId
          ? `http://localhost:5001/api/parties/${partyId}/summary`
          : `http://localhost:5001/api/factories/${factoryId}/summary`;

        const res = await axios.get(url);
        setSummary(res.data);
      } catch (err) {
        setError("Unable to fetch data. Please try again.");
        setSummary(null);
      } finally {
        setLoading(false);
      }
    };

    fetchSummary();
  }, [partyId, factoryId]);

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.text("Hisab Tally Report", 14, 20);
    doc.autoTable({
      head: [["Field", "Value"]],
      body: [
        ["Total Amount", formatCurrency(summary.totalAmount)],
        [partyId ? "Total Paid" : "Total Received", formatCurrency(partyId ? summary.totalPaid : summary.totalReceived)],
        ["Remaining", formatCurrency(summary.remaining)],
      ],
      startY: 30,
    });

    if (summary.transactions?.length) {
      doc.autoTable({
        head: [["Date", "Amount", "Weight", "Rate"]],
        body: summary.transactions.map((tx) => [
          formatDate(tx.date),
          formatCurrency(tx.amount),
          tx.weight,
          formatCurrency(tx.rate),
        ]),
        startY: doc.lastAutoTable.finalY + 10,
      });
    }

    if (summary.payments?.length) {
      doc.autoTable({
        head: [["Date", "Amount"]],
        body: summary.payments.map((p) => [
          formatDate(p.date),
          formatCurrency(p.amount),
        ]),
        startY: doc.lastAutoTable.finalY + 10,
      });
    }

    doc.save("hisab-summary.pdf");
  };

  const exportExcel = () => {
    const wb = XLSX.utils.book_new();

    const summarySheet = [
      { Field: "Total Amount", Value: summary.totalAmount },
      {
        Field: partyId ? "Total Paid" : "Total Received",
        Value: partyId ? summary.totalPaid : summary.totalReceived,
      },
      { Field: "Remaining", Value: summary.remaining },
    ];

    XLSX.utils.book_append_sheet(wb, XLSX.utils.json_to_sheet(summarySheet), "Summary");

    if (summary.transactions?.length) {
      const transactionsSheet = summary.transactions.map((tx) => ({
        Date: formatDate(tx.date),
        Amount: tx.amount,
        Weight: tx.weight,
        Rate: tx.rate,
      }));
      XLSX.utils.book_append_sheet(
        wb,
        XLSX.utils.json_to_sheet(transactionsSheet),
        "Transactions"
      );
    }

    if (summary.payments?.length) {
      const paymentsSheet = summary.payments.map((p) => ({
        Date: formatDate(p.date),
        Amount: p.amount,
      }));
      XLSX.utils.book_append_sheet(
        wb,
        XLSX.utils.json_to_sheet(paymentsSheet),
        "Payments"
      );
    }

    const excelBuffer = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    saveAs(new Blob([excelBuffer], { type: "application/octet-stream" }), "hisab-summary.xlsx");
  };

  if (!partyId && !factoryId) {
    return <p className="text-gray-500">Please select a party or factory to view summary.</p>;
  }

  if (loading) return <p className="text-blue-500">Loading summary...</p>;
  if (error) return <p className="text-red-600">{error}</p>;
  if (!summary) return <p>No data available.</p>;

  const total = summary.totalAmount || 0;
  const paidOrReceived = partyId ? summary.totalPaid : summary.totalReceived;
  const remaining = summary.remaining || 0;

  return (
    <div className="bg-white p-6 rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-4">
        {partyId ? "Party Summary" : "Factory Summary"}
      </h2>

      <SummarySection
        label={partyId ? "Total Paid" : "Total Received"}
        total={total}
        paidOrReceived={paidOrReceived}
        remaining={remaining}
      />

      <div className="flex gap-4 mb-6">
        <button
          onClick={exportPDF}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded shadow"
        >
          ðŸ“„ Export PDF
        </button>
        <button
          onClick={exportExcel}
          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded shadow"
        >
          ðŸ“Š Export Excel
        </button>
      </div>

      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">Transactions</h3>
        {summary.transactions?.length > 0 ? (
          <div className="overflow-auto">
            <table className="min-w-full text-sm border">
              <thead className="bg-gray-100">
                <tr>
                  <th className="border px-3 py-2">Date</th>
                  <th className="border px-3 py-2">Amount</th>
                  <th className="border px-3 py-2">Weight</th>
                  <th className="border px-3 py-2">Rate</th>
                </tr>
              </thead>
              <tbody>
                {summary.transactions.map((tx) => (
                  <tr key={tx.id}>
                    <td className="border px-3 py-2">{formatDate(tx.date)}</td>
                    <td className="border px-3 py-2">{formatCurrency(tx.amount)}</td>
                    <td className="border px-3 py-2">{tx.weight}</td>
                    <td className="border px-3 py-2">{formatCurrency(tx.rate)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p>No transactions available.</p>
        )}
      </div>

      <div>
        <h3 className="text-xl font-semibold mb-2">Payments</h3>
        {summary.payments?.length > 0 ? (
          <div className="overflow-auto">
            <table className="min-w-full text-sm border">
              <thead className="bg-gray-100">
                <tr>
                  <th className="border px-3 py-2">Date</th>
                  <th className="border px-3 py-2">Amount</th>
                </tr>
              </thead>
              <tbody>
                {summary.payments.map((pay) => (
                  <tr key={pay.id}>
                    <td className="border px-3 py-2">{formatDate(pay.date)}</td>
                    <td className="border px-3 py-2">{formatCurrency(pay.amount)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p>No payments available.</p>
        )}
      </div>
    </div>
  );
};

export default HisabTally;
